drills.starterCode:
#include <stdio.h>

int main() {
    int a = 10;
    int b = 20;
    int temp;

    /* YOUR CODE HERE - Swap a and b using temp */

    printf("%d %d\n", a, b);
    return 0;
}

drills.solution:
#include <stdio.h>

int main() {
    int a = 10;
    int b = 20;
    int temp;

    temp = a;
    a = b;
    b = temp;

    printf("%d %d\n", a, b);
    return 0;
}

